import { useState } from "react";
import { Link } from "react-router-dom";
import { ChevronDown, ChevronRight } from "lucide-react";

interface SubSubItem {
  label: string;
  href: string;
}

interface SubItem {
  label: string;
  href: string;
  children?: SubSubItem[];
}

interface NavItemProps {
  label: string;
  href?: string;
  children?: SubItem[];
}

const NavItem = ({ label, href, children }: NavItemProps) => {
  const [isOpen, setIsOpen] = useState(false);
  const [activeSubItem, setActiveSubItem] = useState<string | null>(null);

  if (children) {
    return (
      <div
        className="relative group"
        onMouseEnter={() => setIsOpen(true)}
        onMouseLeave={() => {
          setIsOpen(false);
          setActiveSubItem(null);
        }}
      >
        <button className="flex items-center gap-1 px-4 py-2 text-muted-foreground transition-all duration-300 hover:text-foreground group">
          <span className="relative">
            {label}
            <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-foreground transition-all duration-300 group-hover:w-full" />
          </span>
          <ChevronDown className={`w-4 h-4 transition-transform duration-300 ${isOpen ? 'rotate-180' : ''}`} />
        </button>
        
        {/* First level dropdown */}
        <div className={`absolute top-full left-0 mt-2 min-w-48 bg-card border border-border rounded-lg shadow-lg z-50 overflow-visible transition-all duration-300 ${isOpen ? 'opacity-100 visible translate-y-0' : 'opacity-0 invisible -translate-y-2'}`}>
          {children.map((child) => (
            <div
              key={child.href}
              className="relative"
              onMouseEnter={() => setActiveSubItem(child.label)}
              onMouseLeave={() => setActiveSubItem(null)}
            >
              {child.children ? (
                <div className="flex items-center justify-between px-4 py-3 text-muted-foreground hover:text-foreground hover:bg-secondary transition-all duration-200 cursor-pointer">
                  <span>{child.label}</span>
                  <ChevronRight className="w-4 h-4" />
                </div>
              ) : (
                <Link
                  to={child.href}
                  className="block px-4 py-3 text-muted-foreground hover:text-foreground hover:bg-secondary transition-all duration-200"
                >
                  {child.label}
                </Link>
              )}
              
              {/* Second level dropdown (sub-sub options) - slides to right */}
              {child.children && (
                <div className={`absolute top-0 right-full mr-1 min-w-52 bg-card border border-border rounded-lg shadow-lg z-50 transition-all duration-300 ${activeSubItem === child.label ? 'opacity-100 visible translate-x-0' : 'opacity-0 invisible translate-x-2'}`}>
                  {child.children.map((subChild) => (
                    <Link
                      key={subChild.href}
                      to={subChild.href}
                      className="block px-4 py-3 text-muted-foreground hover:text-foreground hover:bg-secondary transition-all duration-200 first:rounded-t-lg last:rounded-b-lg"
                    >
                      {subChild.label}
                    </Link>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <Link
      to={href || "/"}
      className="relative px-4 py-2 text-muted-foreground transition-all duration-300 hover:text-foreground group"
    >
      <span className="relative">
        {label}
        <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-foreground transition-all duration-300 group-hover:w-full" />
      </span>
    </Link>
  );
};

